EXECUTE('   
CREATE PROCEDURE dbo.SP_ACTIVIDAD_COPIA_A_TEMPORAL  
   @P_CODIGO NUMERIC(12),
   @P_ORDINAL NUMERIC(3),
   @P_USUARIO VARCHAR(10)
AS 
   BEGIN

    INSERT INTO dbo.TEMP_CLI_ACTIVIDAD_ECONOMICA ( CODIGO_USUARIO, TZ_LOCK, CODIGO_PERSONA_CLIENTE, CODIGO_ACTIVIDAD, ORDINAL_ACTIVIDAD, EMPRESA, CARGO, FECHAINGRESO, FECHAEGRESO, SALARIO, MONEDA, FECHAACTUALIZACIONINGRESOS, FUENTE_INGRESO, INGRESO_LIQUIDO_PESOS, RUC_INDEPENDIENTE, INICIO_ACTIVIDAD, NRO_CAJA_PROFESIONAL, INGRESO_PROM_PESOS, TIPO_DE_RENTA, UBICACION_DEL_BIEN, VTO_CONTRATO, PADRON, GIRO, NRO_BPS, PEQUEÑA_EMPRESA_DGI, VENTA_ANUAL, COSTO_VENTA_ANUAL, PRESENTA_BAL_DECL_JURADA, TIPO_DE_VIVIENDA, CUOTA_HIPOTECA, CUOTA_ALQUILER, PERSONAS_A_CARGO, NRO_HIJOS, REQUIERE_CODEUDOR, P_ESTADO_PATR_CERT, CERTIFICADO_AFIP, VTO_CERT_AFIP, TIPO_DE_PERSONA, COD_PAIS, F_GRUPO_ACT_EC, CONSTANCIA_INGRESOS, IMPORTE, INGRESOS_CERTIFICADOS, VIATICOS, PARTIDAS_EXTRAORDINARIAS, HORAS_EXTRAS, PARTIDAS_VARIABLES, COMISIONES_COBRADAS, ADELANTO_SUELDOS, VIATICOS_SIN_RENDICION, INGRESO_NOMINAL, VTAS_ANUALES_UI, DIRECCION, CANTIDAD_EMPLEADOS, ORIGEN_FUNTE_INGRESO, TIPO_FISJUR, FECHA_ACT_ECONOMICA, CONSEJO_PROFESIONAL, NRO_MAT_PROFESIONAL, FECHA_INSCRIPCION_CNA, FECHA_VENCIMIENTO_CNA, IERIC, FECHA_INICIO_IERIC, FECHA_VENC_IERIC, CARACTER_IERIC, VERIFICADO_AFIP, FECHA_VERIFICADO, ORDEN_AFIP, ESTADO_REGISTRO, CUIL)
	SELECT @P_USUARIO, *, ''M'', (SELECT TOP 1 NUMERODOCUMENTO FROM CLI_DocumentosPFPJ WHERE NUMEROPERSONAFJ=@P_CODIGO AND (TIPODOCUMENTO=''CUIL'' OR TIPODOCUMENTO=''CUIT'') AND ((TZ_LOCK < 300000000000000 OR TZ_LOCK >= 400000000000000) AND (TZ_LOCK < 100000000000000 OR TZ_LOCK >= 200000000000000))) FROM CLI_ACTIVIDAD_ECONOMICA WHERE CODIGO_PERSONA_CLIENTE=@P_CODIGO AND ORDINAL_ACTIVIDAD=@P_ORDINAL
	
   END


')
